app.controller("windowCtrl",["$scope","Window",function(i,n){i.classes="max",i.close=function(){n.window.close()},i.min=function(){n.window.minimize()},i.max=function(){"max"==i.classes?n.window.maximize():n.window.restore()},n.listen("maximize",function(){i.classes="restore"}),n.listen("unmaximize",function(){i.classes="max"})}]);